//Promesas

import {getHeroeById} from './bases/08-imp-exp'

const promesa = new Promise ( (resolve, reject) => {
  setTimeout( () => {
    const my_heroe = getHeroeById (2);
    //console.log(heroe); 

    /**
    * Una vez encontrado el heroe lo maandamos al resolve como argumento
    * de esta manera podemos acceder a lo que tenga heroe en promesa.then
    */
    //resolve(my_heroe);
     /**
      * Si no encuentra el heroe entonces utilizamos reject para manejar el 
      * error. Reject se utiliza cuando la promesa no se cumple. Para ello
      * debemos utilizar el metodo catch en el then para manejar el error. 
      */
     reject ('No se pudo encontrar el heroe')
  }, 2000)
  
});

promesa.then ( (heroe) =>{
  console.log('El heroe es:', heroe);
}).catch ( err => console.warn (err) )

