const nombre    = 'Yohandys';
const apellidos = 'Martinez Navarro';

/*Al utilizar 'let' debemos tener presente que son variables scope o de ambientes
  en particular, es decir que solo el valor de la variable va a cambiar en 
  ambientes diferentes.
*/
let valorDado  = 5;
 /*Si volvemos a poner: let valorDado =8; devuelve un error pues estamos utilizando
   la misma variable (let valorDado) en el mismo ambiente.
   Seria solo poner: valorDado = 8;
 */
valorDado =88;

console.log(nombre, apellidos, valorDado);
